INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (27846, 1, 0, 'Where are you?', 12, 0, 100, 1, 0, 0, 0, 0, 'Text 2');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (27987, 0, 0, 'Over here, to the southeast. I see they got you too...', 14, 0, 100, 0, 0, 0, 27610, 0, 'Monte Muzzleshot');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (27996, 0, 1, 'Across this vast expanse rest the bones of my ancestors. Their spirits have ascended to the Chamber of the Aspects.', 12, 0, 100, 0, 0, 0, 27394, 0, 'WYRMREST VANQUISHER');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (27996, 0, 2, 'Galakrond was the progenitor of dragonkind. It was from Galakrond that the Titans shaped the five Aspects.', 12, 0, 100, 0, 0, 0, 27396, 0, 'WYRMREST VANQUISHER');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (27996, 0, 3, 'It truly is a shame that the blue dragonflight has chosen such a heartless, cold path. It pains me greatly to do battle with Malygos\'s children.', 12, 0, 100, 0, 0, 0, 27400, 0, 'WYRMREST VANQUISHER');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (27996, 0, 4, 'The queen and her council have been speaking lately of a disaster at Ulduar. Something has happened in Storm Peaks!', 12, 0, 100, 0, 0, 0, 27399, 0, 'WYRMREST VANQUISHER');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (27996, 0, 5, 'Today is a good day, friend. Long has it been since I have left Wyrmrest Temple, free to fly across the great Dragonblight.', 12, 0, 100, 0, 0, 0, 27398, 0, 'WYRMREST VANQUISHER');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (27996, 0, 6, 'What the Scourge has done here will reverberate throughout our world. The aberrations must be stopped!', 12, 0, 100, 0, 0, 0, 27395, 0, 'WYRMREST VANQUISHER');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (27996, 1, 0, 'You must remember that I have yet to mature into an elder wyrm. I must eat to renew my strength!', 12, 0, 100, 0, 0, 0, 27397, 0, 'WYRMREST VANQUISHER');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (27996, 2, 0, 'I have been called back to Wyrmrest, ally. Farewell!', 12, 0, 100, 0, 0, 0, 27404, 0, 'WYRMREST VANQUISHER');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (28006, 0, 0, 'You think you\'ve won, mortal? Face the unbridled power of Antiok!', 14, 0, 100, 0, 0, 0, 27415, 0, 'Antiok Yell1');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (28006, 0, 1, 'Behold! The Scythe of Antiok!', 14, 0, 100, 0, 0, 0, 27416, 0, 'Antiok Yell2');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (28006, 1, 0, 'Soon, the bones of Galakrond will rise from their eternal slumber and wreak havoc upon this world!', 14, 0, 100, 0, 0, 0, 27406, 0, 'Antiok Yell3');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (28006, 1, 1, 'The Lich King demands more frost wyrms be sent to Angrathar! Meet his demands or face my wrath!', 14, 0, 100, 0, 0, 0, 27408, 0, 'Antiok Yell4');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (28006, 1, 2, 'Faster, dogs! We mustn\'t relent in our assault against the interlopers!', 14, 0, 100, 0, 0, 0, 27405, 0, 'Antiok Yell5');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (28006, 1, 3, 'Attackers are upon us! Let none through to this ancient grave!', 14, 0, 100, 0, 0, 0, 27409, 0, 'Antiok Yell6');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (28006, 1, 4, 'Hear me, minions! Hear your lord, Antiok! Double your efforts or pay the consequences of failure!', 14, 0, 100, 0, 0, 0, 27407, 0, 'Antiok Yell7');
