INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (28192, 0, 0, 'WARNING: Missile lock detected. Engaging evasive maneuvers.', 12, 0, 100, 0, 0, 0, 27621, 0, 'Archimage Pentarus Flying Machine');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (28229, 0, 0, 'More intruders? You\'ll soon learn, no one messes with the Venture Company!', 14, 0, 100, 0, 0, 0, 27611, 0, 'Venture Co. Air Patrol');
