INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (33480, 2, 0, 'It was a powerful feelin\' while I was listenin\' to yer sermon, and it\'s drivin\' me nuts!', 12, 0, 100, 0, 0, 0, 34257, 0, 'Ironforge Champion');
