INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34571, 0, 0, 'Welcome back, $n.  You were fortunate.  Krennan\'s treatment doesn\'t always work this well on people who\'ve had the Curse as long as you.', 12, 0, 100, 1, 0, 0, 36618, 0, 'Gwen Armstead to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34574, 3, 0, 'Our immortality-the very essence of our beings!-was lost.', 12, 0, 100, 1, 0, 0, 34794, 0, 'Shade of the Kaldorei');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34574, 1, 0, 'Much has changed for our people since the Battle of Mount Hyjal.', 12, 0, 100, 1, 0, 0, 34792, 0, 'Shade of the Kaldorei');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34574, 2, 0, 'Nordrassil lies a pale shadow of what it once was, its power used to defeat Archimonde and drive back the Burning Legion.', 12, 0, 100, 1, 0, 0, 34793, 0, 'Shade of the Kaldorei');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34574, 0, 0, '%s fades into existence as you approach, nodding a subtle greeting.', 16, 0, 100, 0, 0, 0, 34832, 0, 'Shade of the Kaldorei');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34574, 4, 0, 'The Betrayer was freed from his prison, and Shand\'do Stormrage disappeared into the emerald dream.', 12, 0, 100, 1, 0, 0, 34795, 0, 'Shade of the Kaldorei');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34574, 5, 0, 'A dark time for all.', 12, 0, 100, 1, 0, 0, 34796, 0, 'Shade of the Kaldorei');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34574, 6, 0, 'The Shade of the Kaldorei closes its eyes and fades away.', 16, 0, 100, 0, 0, 0, 34820, 0, 'Shade of the Kaldorei');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34583, 0, 0, '%s stares into the moonwell, lost in distant memories.', 16, 0, 100, 0, 0, 0, 34819, 0, 'Shade of the Kaldorei');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34583, 1, 0, 'The Arch Druid approached the dragons for their blessings, as the dragons had placed on Nordrassil in ancient times.', 12, 0, 100, 1, 0, 0, 34801, 0, 'Shade of the Kaldorei');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34583, 2, 0, 'But Nozdormu, Lord of Time, refused to give his blessing, chiding the druid for his arrogance.', 12, 0, 100, 1, 0, 0, 34802, 0, 'Shade of the Kaldorei');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34583, 3, 0, 'In agreement, Alexstrasza also refused Staghelm, and without her blessing, Teldrassil\'s growth has been flawed and unpredictable.', 12, 0, 100, 1, 0, 0, 34803, 0, 'Shade of the Kaldorei');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34583, 4, 0, 'Staghelm retreated to his enclave in Darnassus, ever seeking a new direction by which to bless Teldrassil, and restore the immortality of our people.', 12, 0, 100, 1, 0, 0, 34804, 0, 'Shade of the Kaldorei');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34583, 5, 0, 'The Shade of the Kaldorei closes its eyes and fades away.', 16, 0, 100, 0, 0, 0, 34820, 0, 'Shade of the Kaldorei');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34584, 0, 0, '%s approaches the moonwell slowly, a familiar, thoughtful look upon its face.', 16, 0, 100, 0, 0, 0, 34834, 0, 'Shade of the Kaldorei');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34584, 1, 0, 'Over time, Staghelm only grew more disconnected from our people.', 12, 0, 100, 1, 0, 0, 34805, 0, 'Shade of the Kaldorei');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34584, 2, 0, 'Some pose that he blames us, his people, for the loss of his son, long ago during the War of the Shifting Sands.', 12, 0, 100, 1, 0, 0, 34806, 0, 'Shade of the Kaldorei');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34584, 3, 0, 'Others cite rumors of his escalating conflicts with Tyrande Whisperwind, the shining light of our people, our leader in Darnassus.', 12, 0, 100, 1, 0, 0, 34807, 0, 'Shade of the Kaldorei');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34584, 4, 0, 'But many druids of the Cenarion Circle stand firm that he wants only what is best for all of the Kaldorei, and serve him unerringly towards that end.', 12, 0, 100, 1, 0, 0, 34808, 0, 'Shade of the Kaldorei');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34584, 5, 0, 'Fandral Staghelm carries the burden of our sins and our follies, and in so doing deserves the respect of us all.', 12, 0, 100, 1, 0, 0, 34809, 0, 'Shade of the Kaldorei');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34584, 6, 0, 'The Shade of the Kaldorei closes its eyes and fades away.', 16, 0, 100, 66, 0, 0, 34820, 0, 'Shade of the Kaldorei');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34585, 0, 0, '%s rests on the side of the moonwell in peaceful contemplation.', 16, 0, 100, 0, 0, 0, 34833, 0, 'Shade of the Kaldorei');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34585, 1, 0, 'In weeks past, all of those living atop Teldrassil have found their memories blurry, as if time itself seized a portion of their lives.', 12, 0, 100, 1, 0, 0, 34810, 0, 'Shade of the Kaldorei');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34585, 2, 0, 'We break from this haze to find that Fandral Staghelm and many of his closest followers have disappeared without a word.', 12, 0, 100, 1, 0, 0, 34811, 0, 'Shade of the Kaldorei');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34585, 3, 0, 'But in his place, Shan\'do Stormrage awoke from the dream and returned to his beloved Tyrande Whisperwind.', 12, 0, 100, 1, 0, 0, 34812, 0, 'Shade of the Kaldorei');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34585, 4, 0, 'And with his return came Alexstrasza and Ysera, to at long last offer their blessings upon Teldrassil', 12, 0, 100, 1, 0, 0, 34813, 0, 'Shade of the Kaldorei');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34585, 5, 0, 'Though a small corruption lingers, desperately longing to be cleansed, the boughs of Teldrassil itself seem changed. Growth has begun anew.', 12, 0, 100, 1, 0, 0, 34814, 0, 'Shade of the Kaldorei');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34585, 6, 0, 'None know what to expect next, but with Malfurion and Tyrande reunited, a new hope dawns on the Kaldorei.', 12, 0, 100, 1, 0, 0, 34815, 0, 'Shade of the Kaldorei');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34585, 7, 0, 'Hope for lasting relief from the shadow that has been cast on our people for so long.', 12, 0, 100, 1, 0, 0, 44123, 0, 'Shade of the Kaldorei');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34585, 8, 0, 'The Shade of the Kaldorei closes its eyes and fades away.', 16, 0, 100, 0, 0, 0, 34820, 0, 'Shade of the Kaldorei');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34586, 0, 0, '%s smiles at you knowingly as it approaches.', 16, 0, 100, 0, 0, 0, 34835, 0, 'Shade of the Kaldorei');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34586, 1, 0, 'With Malfurion Stormrage gone, the druids were left in disarray and in desperate need of a new leader.', 12, 0, 100, 1, 0, 0, 34797, 0, 'Shade of the Kaldorei');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34586, 2, 0, 'Arch Druid Fandral Staghelm roused the druids and convinced the Circle of Ancients in Darkshore that it was time for our people to rebuild.', 12, 0, 100, 1, 0, 0, 34798, 0, 'Shade of the Kaldorei');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34586, 3, 0, 'In an effort to regain immortality for our people, and with the approval of the Circle, Staghelm and the most powerful druids grew Teldrassil, upon whose boughs you now stand.', 12, 0, 100, 1, 0, 0, 34799, 0, 'Shade of the Kaldorei');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34586, 4, 0, 'Though we have our new home, our immortal lives have not been restored.', 12, 0, 100, 1, 0, 0, 34800, 0, 'Shade of the Kaldorei');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34586, 5, 0, 'The Shade of the Kaldorei closes its eyes and fades away.', 16, 0, 100, 0, 0, 0, 34820, 0, 'Shade of the Kaldorei');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34590, 0, 0, '%s attempts to run away in fear!', 16, 0, 100, 0, 0, 0, 0, 0, 'combat Flee');
