INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34668, 7, 0, 'The Trade Prince\'s yacht is about to set sail. Everyone,get to the dock! Hand over the keys,boss. I\'m driving. Just let me know when you\'re ready to go.', 12, 0, 100, 1, 0, 0, 0, 0, 'say Text');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34668, 6, 0, 'I can\'t stand to watch. So many good memories. So much profit.', 12, 0, 100, 1, 0, 0, 0, 0, 'say Text');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34668, 5, 0, '$N, you have to hurry and break your moolah out of the bank!', 12, 0, 100, 1, 0, 0, 0, 0, 'say Text');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34668, 4, 0, 'You better get upstairs pronto,boss. Trade Prince Gallywix is waiting for you.', 12, 0, 100, 1, 0, 0, 0, 0, 'say Text');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34668, 3, 0, 'Go get \'em,boss!', 12, 0, 100, 1, 0, 0, 0, 0, 'say Text');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34668, 2, 0, 'Ace,Izzy,Gobber,you three make sure you tag a long with $N and help him take care of those dead beats!', 12, 0, 100, 1, 0, 0, 0, 0, 'say Text');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34668, 0, 0, 'Haha! that should be a blast!', 12, 0, 100, 25, 0, 0, 0, 0, 'say Text');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34668, 1, 0, 'He\'ll get me more later? How are we supposed to get you promoted to Trade Prince without our number one product on the shelves?', 12, 0, 100, 1, 0, 0, 0, 0, 'say Text');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34689, 0, 0, 'All what you have to do is let me show you how was a Visca cooperation. Then, head to one of the models to lead training on which Bamm is going to pull, start a pernicious attack and finish it with a Visca cooperation.', 12, 0, 100, 1, 0, 0, 0, 0, 'say Text');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34693, 0, 0, 'I\'ll address the CAP so that it activates automatically when you\'re at the villa. Do just well warning avoid pigs!', 12, 0, 100, 1, 0, 0, 0, 0, 'say Text');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34693, 1, 0, 'All what you have to do is let me show you how was a Visca cooperation. Then, head to one of the models to lead training on which Bamm is going to pull, start a pernicious attack and finish it with a Visca cooperation.', 12, 0, 100, 1, 0, 0, 0, 0, 'say Text');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34695, 0, 0, 'Would one of you hurry up and finish off the other? Some of us are trying to make some moolah here!', 12, 0, 100, 1, 0, 0, 0, 0, 'say Text');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34697, 0, 0, 'Insert your coins inside the Warrior NX-01, who\'s learn you. Then use the technique on one of the models to lead training.', 12, 0, 100, 0, 0, 0, 0, 0, 'say Text');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34697, 3, 3, 'Rap... rap...', 0, 0, 100, 0, 0, 0, 0, 0, '');
