INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34748, 0, 0, '$n, it\'s you! Thanks, $g man : sweetie;!', 12, 0, 100, 0, 0, 0, 35003, 0, 'Goblin Survivor to Player');
