INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (36283, 0, 0, 'You must return to the Duskmist Shore!', 42, 0, 100, 0, 0, 0, 50651, 0, 'Forsaken Catapult to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (36287, 1, 1, 'You are scary!  I just want my mommy!', 12, 0, 100, 18, 0, 23508, 0, 0, 'Cynthia to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (36287, 0, 1, 'It\'s not safe here. Go to Allen\'s basement.', 12, 0, 100, 0, 0, 0, 0, 0, 'Player to Cynthia');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (36288, 1, 1, 'Are you one of the good worgen, $G mister:ma\'am;?  Did you see Cynthia hiding in the sheds outside?', 12, 0, 100, 0, 0, 23508, 0, 0, 'Ashley to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (36288, 0, 1, 'Join the others inside the basement next door. Hurry!', 12, 0, 100, 0, 0, 0, 0, 0, 'Player to Ashley');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (36289, 0, 1, 'Your mother\'s in the basement next door. Get to her now!', 12, 0, 100, 0, 0, 0, 0, 0, 'Player to James');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (36289, 1, 1, 'Don\'t hurt me!  I was just looking for my sisters!  I think Ashley\'s inside that house!', 12, 0, 100, 0, 0, 23508, 0, 0, 'James to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (36290, 0, 0, 'Hold your positions, men!', 14, 0, 100, 0, 0, 23539, 0, 0, 'Lord Godfrey to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (36291, 0, 0, 'What\'s happening?!', 12, 0, 100, 430, 0, 23539, 0, 0, 'Melinda Hammond to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (36294, 0, 0, 'Who dares to touch Koroth\'s banner!?', 14, 0, 100, 0, 0, 19605, 0, 0, 'Koroth the Hillbreaker to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (36294, 1, 0, 'You puny thief!  Koroth find you!  Koroth smash your face in!', 14, 0, 100, 0, 0, 19605, 0, 0, 'Koroth the Hillbreaker to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (36330, 0, 0, 'Give it up, Krennan.  It\'s time to put this one down.  It\'s protocol.', 12, 0, 100, 274, 0, 0, 36336, 0, 'Lord Godfrey to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (36331, 0, 0, 'I am not giving up on you.  I don\'t have a cure for the Curse yet... but there are treatments.  You will have control again.', 12, 0, 100, 274, 0, 0, 36344, 0, 'Krennan Aranas to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (36332, 0, 0, 'Tell me, Godfrey.  Those that stayed in Gilneas City so that we could live.  Were they following protocol?', 12, 0, 100, 1, 0, 0, 36341, 0, 'King Genn Greymane to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (36332, 1, 0, 'I didn\'t think so.  Now hand me that potion, Krennan... and double the dosage.', 12, 0, 100, 1, 0, 0, 36341, 0, 'King Genn Greymane to Player');
