INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (36403, 0, 0, 'I\'ve got my eye on you, $n. Let\'s just say that only one of us will be leaving this island, and that goblin\'s name begins with Trade Prince!', 14, 0, 100, 0, 0, 0, 36382, 0, 'Trade Prince Gallywix to Player');
