INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39193, 1, 0, 'Break time over!', 12, 0, 100, 0, 0, 0, 39322, 0, 'Brute Overseer to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39193, 2, 0, 'Me like beat you up!', 12, 0, 100, 0, 0, 0, 39320, 0, 'Brute Overseer to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39193, 3, 0, 'Me overseer, you slave!', 12, 0, 100, 0, 0, 0, 39321, 0, 'Brute Overseer to Player');
