INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39194, 0, 0, 'What? You\'re not supposed to be here. You\'re dead!', 12, 0, 100, 0, 0, 0, 39340, 0, 'Blastshadow the Brutemaster to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39195, 0, 0, 'Kill that goblin, quickly!', 12, 0, 100, 0, 0, 0, 39341, 0, 'Delicia Whipsnaps to Player');
