INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39198, 0, 0, 'Feed pigs rubber, \'til they bounce. There\'s got to be an application for that.', 14, 0, 100, 0, 0, 0, 37343, 0, 'Ace to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39199, 0, 0, 'I\'VE GOT AN IDEA!', 14, 0, 100, 0, 0, 0, 39259, 0, 'Assistant Greely to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39199, 1, 0, 'We\'ve got to get our people out of here, $n! The key is to get them to drink plenty of Kaja\'Cola Zero-One!', 12, 0, 100, 5, 0, 0, 50102, 0, 'Assistant Greely to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39199, 2, 0, 'We\'re not a part of your system!', 14, 0, 100, 15, 0, 0, 39312, 0, 'Assistant Greely to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39199, 3, 0, '$n, we need to escape through the northern tunnel!', 12, 0, 100, 1, 0, 0, 50103, 0, 'Assistant Greely to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39201, 0, 0, 'A binary number system. It uses only two numbers. 6 and 11.', 14, 0, 100, 0, 0, 0, 37358, 0, 'Gobber to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39269, 0, 0, 'Tiragarde belongs to Northwatch now!', 12, 0, 100, 0, 0, 0, 39476, 0, 'Lieutenant Palliter');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39270, 1, 0, 'Thank you, $r... I was close to death.  May I stay by your side for a moment?', 12, 0, 100, 0, 0, 0, 0, 0, 'Injured Razor Hill Grunt');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39270, 0, 2, 'H-help me!', 12, 0, 100, 0, 0, 0, 0, 0, 'Injured Razor Hill Grunt');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39270, 0, 1, 'Somebody... anybody...', 12, 1, 100, 0, 0, 0, 0, 0, 'Injured Razor Hill Grunt');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39270, 0, 0, 'I can\'t fight any longer...', 12, 1, 100, 0, 0, 0, 0, 0, 'Injured Razor Hill Grunt');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39270, 1, 1, 'You\'ve saved my life, $c.  Protect me for a few more moments while I recover.', 12, 0, 100, 0, 0, 0, 0, 0, 'Injured Razor Hill Grunt');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39270, 1, 2, 'For the Warchief! You lead, I will follow.', 12, 0, 100, 0, 0, 0, 0, 0, 'Injured Razor Hill Grunt');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39270, 1, 3, 'I was supposed to die in battle... ah well, another day.  I will follow you until I recover.', 12, 0, 100, 0, 0, 0, 0, 0, 'Injured Razor Hill Grunt');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39270, 2, 0, 'Thank you again. I will return to Razor Hill now.', 12, 1, 100, 0, 0, 0, 0, 0, 'Injured Razor Hill Grunt');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39270, 2, 1, 'I feel much better now.  For the Horde!', 12, 1, 100, 0, 0, 0, 0, 0, 'Injured Razor Hill Grunt');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39270, 2, 2, 'It was an honor to watch you fight, $n.', 12, 1, 100, 0, 0, 0, 0, 0, 'Injured Razor Hill Grunt');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39270, 2, 3, 'You fight well.  I will leave you now.  Lok\'tar ogar!', 12, 1, 100, 0, 0, 0, 0, 0, 'Injured Razor Hill Grunt');
