INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39353, 0, 0, 'What\'s the matter, chum... Never seen a worgen before?', 12, 0, 100, 0, 0, 0, 0, 0, 'on Aggro Text');
