INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39395, 0, 0, 'We are all a part of him...', 12, 0, 50, 0, 0, 0, 0, 0, 'on Aggro Text');
