INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39582, 1, 0, 'I SEE THE TRAITOR IS HERE TO RESCUE YOU, WARCHIEF. HOW CONVENIENT. YOU WILL BOTH BOW TO ME OR FALL TOGETHER!', 14, 0, 100, 0, 0, 19567, 39530, 0, 'Trade Prince Gallywix');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39582, 2, 0, 'I\'m downsizing the cartel, starting with you!', 14, 0, 100, 0, 0, 19571, 39556, 0, 'Trade Prince Gallywix to Thrall');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39582, 3, 0, 'I like you. Here\'s a raise!', 14, 0, 100, 0, 0, 19578, 39573, 0, 'Trade Prince Gallywix to Thrall');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39582, 4, 0, 'Excuse me while I dispose of these toxic assets all over you!', 14, 0, 100, 0, 0, 19569, 39559, 0, 'Trade Prince Gallywix');
