INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (41578, 0, 0, 'The Frostmane trolls have been raiding the ranch continuously.', 12, 0, 100, 6, 0, 0, 41525, 0, 'Sergeant Flinthammer to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (41578, 1, 0, 'As it is, there are barely enough of us to keep them from taking everything.', 12, 0, 100, 5, 0, 0, 41526, 0, 'Sergeant Flinthammer to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (41578, 2, 0, 'They just take the rams and go. The herd can\'t take any more.', 12, 0, 100, 1, 0, 0, 41527, 0, 'Sergeant Flinthammer to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (41578, 3, 0, 'One of the mountaineers has seen something. Keep your eyes open!', 14, 0, 100, 25, 0, 0, 41528, 0, 'Sergeant Flinthammer to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (41603, 0, 0, 'If you want your rams back, come get them, cowards!', 14, 0, 100, 15, 0, 0, 41519, 0, 'Frostmane Raider to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (41611, 0, 0, 'Does anyone hear something strange?', 12, 0, 100, 6, 0, 0, 41521, 0, 'Amberstill Mountaineer to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (41611, 1, 0, 'There it is again! I know I heard something!', 12, 0, 100, 5, 0, 0, 41522, 0, 'Amberstill Mountaineer to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (41611, 2, 0, 'Frostmane raiders! Sound the alarm!', 14, 0, 100, 5, 0, 7234, 41523, 0, 'Amberstill Mountaineer to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (41611, 3, 0, 'To arms, mountaineers! Take back those rams!', 14, 0, 100, 25, 0, 0, 41524, 0, 'Amberstill Mountaineer to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (41924, 0, 0, '%s becomes enraged!', 16, 0, 100, 0, 0, 0, 0, 0, 'combat Enrage');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (42010, 0, 0, 'Long live the Dark Iron!', 12, 0, 100, 15, 0, 0, 41821, 0, 'General Grimaxe to Dark Iron Golem');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (42129, 0, 0, 'How dare you storm into my throne room and attempt to spread this conspiratorial nonsense!', 12, 7, 100, 5, 0, 0, 42005, 0, 'Moira Thaurissan to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (42129, 1, 0, 'I will not let you and this treasonous Commander Stonebreaker steal my son\'s birthright!', 12, 0, 100, 274, 0, 0, 42006, 0, 'Moira Thaurissan to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (42129, 2, 0, 'Both of you know I would never order such a thing! Why would I set one half my son\'s inheritance against the other?', 12, 7, 100, 5, 0, 0, 42022, 0, 'Moira Thaurissan to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (42129, 3, 0, 'No, this is the work of a traitor to both our peoples. Despite my best efforts, some Dark Irons remain in the grip of Twilight\'s Hammer.', 12, 0, 100, 1, 0, 0, 42026, 0, 'Moira Thaurissan to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (42129, 4, 0, 'The cult must have someone in the city to coordinate this kind of attack. That only leaves one suspect, Ambassador Slaghammer!', 12, 0, 100, 1, 0, 0, 42028, 0, 'Moira Thaurissan to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (42129, 5, 0, 'If we were in Shadowforge City, I would have you tossed into the Ring of Law for your transgressions, Ambassador. The darkest dungeons of Ironforge will have to do for now.', 12, 0, 100, 274, 0, 0, 42044, 0, 'Moira Thaurissan to Arrest the Ambassador Credit');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (42131, 0, 0, 'Muradin has a point. If you cannot govern your own people, you have no place at this table.', 12, 7, 100, 273, 0, 0, 42017, 0, 'Falstad Wildhammer to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (42153, 0, 0, 'I go willingly, $c. My purpose in my master\'s plan is fulfilled.', 12, 0, 100, 1, 0, 0, 42046, 0, 'Ambassador Slaghammer to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (42153, 1, 0, 'Do as you will with me. I am not afraid. That halfbreed runt will never be my emperor! Long live Twilight\'s Hammer!', 12, 0, 100, 5, 0, 0, 42043, 0, 'Ambassador Slaghammer to Arrest the Ambassador Credit');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (42230, 0, 0, '%s becomes enraged!', 16, 0, 100, 0, 0, 0, 0, 0, 'combat Enrage');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (42231, 0, 0, '%s goes into a frenzy!', 16, 0, 100, 0, 0, 0, 0, 0, 'combat Frenzy');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (42366, 0, 0, 'We\'re going to have a hard time handling this one alone. I sent a message to the dwarven capital of Ironforge asking for their support.', 12, 0, 100, 1, 0, 0, 43111, 0, 'Kelsey Steelspark to Captain Tread Sparknozzle');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (42371, 0, 0, 'Father...', 12, 0, 100, 0, 0, 0, 38143, 0, 'Vanessa van Cleef to Edwin van Cleef');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (42391, 0, 0, 'Down with the king!', 0, 0, 100, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (42391, 1, 1, 'Get off my land!', 0, 0, 100, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (42489, 0, 0, 'We can\'t afford to let our enemy, Thermaplugg, continue to tighten his grip on the rest of Gnomeregan.', 12, 0, 100, 1, 0, 0, 43103, 0, 'Captain Tread Sparknozzle');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (42489, 1, 0, 'He has ordered his crony, Razlo Crushcog, to keep us on the defensive. Hinkles, what\'s the latest intelligence on Razlo and his men?', 12, 0, 100, 1, 0, 0, 43104, 0, 'Captain Tread Sparknozzle');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (42489, 2, 0, 'We have to be in position to strike against Crushcog when the mountaineers arrive.', 12, 0, 100, 1, 0, 0, 43118, 0, 'Captain Tread Sparknozzle to Captain Tread Sparknozzle');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (42489, 3, 0, 'With Crushcog defeated, Thermaplugg is sure to be quaking in his mechano-tank, and rightly so. You\'re next, Thermaplugg. You\'re next!', 12, 0, 100, 1, 0, 0, 43119, 0, 'Captain Tread Sparknozzle to Captain Tread Sparknozzle');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (42490, 0, 0, 'A messenger reached us from Brewnall Village this morning. The dwarves are sending a squad of mountaineers to help us.', 12, 0, 100, 1, 0, 0, 43117, 0, 'Elgin Clickspring to Captain Tread Sparknozzle');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (42491, 0, 0, 'It\'s been hard for our scouts to get information on what Crushcog is planning.', 12, 0, 100, 1, 0, 0, 43105, 0, 'Hinkles Fastblast to Captain Tread Sparknozzle');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (42491, 1, 0, 'What we do know is that Crushcog is using an advanced mechano-tank prototype and his irradiated gnomes are trying to repair more at the old arsenal.', 12, 0, 100, 1, 0, 0, 43110, 0, 'Hinkles Fastblast to Captain Tread Sparknozzle');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (42536, 0, 0, '%s becomes enraged!', 16, 0, 100, 0, 0, 0, 0, 0, 'combat Enrage');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (42553, 3, 0, 'Uh... a couple more tweaks should do it, I think...', 12, 0, 100, 6, 0, 0, 42950, 0, 'Engineer Grindspark');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (42553, 1, 0, 'At last, it\'s finished.', 12, 0, 100, 25, 0, 0, 42948, 0, 'Engineer Grindspark');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (42553, 2, 0, 'Arise my, uh... what shall I call you? How about \'multi-bot\'? Arise, my multi-bot!', 12, 0, 100, 5, 0, 0, 42949, 0, 'Engineer Grindspark');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (42553, 0, 0, 'Time to put the finishing touches on my bot.', 12, 0, 100, 5, 0, 0, 42947, 0, 'Engineer Grindspark');
