INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (43566, 2, 0, 'I want two sharpshooters to stay behind and cover the deck.  Everyone else, use the ropes to rappel down.  Let\'s give them hell!', 14, 0, 100, 0, 0, 16419, 0, 0, 'Lorna Crowley');
