INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (48494, 19, 0, 'Well of course we are, my dear Greely. And look there. The Micro Mechachicken\'s stabilization appears to be a complete success!', 12, 0, 100, 1, 0, 0, 0, 0, 'say Text');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (48494, 21, 0, 'I haven\'t the foggiest notion what you\'re talking about, Miss Greely.', 12, 0, 100, 1, 0, 0, 0, 0, 'say Text');
