INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (48496, 7, 0, 'Ya think? Hobart, the Micro Mechachicken is destabilizing. It\'s expanding!', 12, 0, 100, 1, 0, 0, 0, 0, 'say Text');
