INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (50372, 0, 0, 'Rise from the grave and serve the Dark Lady!', 12, 0, 100, 1, 0, 0, 50118, 0, 'Aradne');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (50372, 0, 2, 'Rise from death\'s slumber and join your brothers!', 12, 0, 100, 1, 0, 0, 50120, 0, 'Aradne');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (50372, 0, 3, 'Waken, sleeper. Your new life awaits.', 12, 0, 100, 1, 0, 0, 50121, 0, 'Aradne');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (50372, 0, 4, 'You are returned to life. The Banshee Queen asks for your service.', 12, 0, 100, 1, 0, 0, 50122, 0, 'Aradne');
