INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (50415, 0, 0, 'You\'ve been bitten by a worgen.  It\'s probably nothing, but it sure stings a little.$B$B|TInterface\\Icons\\INV_Misc_monsterfang_02.blp:32|t', 42, 0, 100, 0, 0, 0, 50192, 0, 'Josiah Event Trigger to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (50474, 0, 0, 'Hold them back!  We must protect the survivors!', 14, 0, 100, 0, 0, 0, 50264, 0, 'Gilneas City Guard');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (50504, 0, 0, 'Ishala! Ishala! Are you out there?!? Please, Ishala -- listen to my voice!', 12, 0, 100, 22, 0, 0, 0, 0, 'Belysra Starbreeze');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (50504, 1, 0, 'I cannot leave without my child! Please, somebody, help!', 12, 0, 100, 22, 0, 0, 0, 0, 'Belysra Starbreeze');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (50893, 0, 0, 'May the Light bless the spirits of our ancestors, for they\'ve chosen to allow my son to rest upon this holy ground.', 12, 0, 100, 1, 0, 19605, 0, 0, 'King Genn Greymane to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (51409, 0, 0, 'Look out! We\'re under attack!', 14, 0, 100, 0, 0, 19605, 0, 0, 'Lorna Crowley to Stagecoach Carriage');
