INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (55944, 0, 0, 'We need more healers at the tents. Recall the priests from the west.', 14, 0, 100, 22, 0, 27508, 60625, 0, 'Delora Lionheart');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (55944, 1, 0, 'Hold the line, sailors! Your brothers depend on you!', 14, 0, 100, 22, 0, 27505, 59721, 0, 'Delora Lionheart');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (55944, 2, 0, 'Tend to the wounded! This isn\'t over yet.', 14, 0, 100, 22, 0, 27506, 59722, 0, 'Delora Lionheart');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (55944, 3, 0, 'We have men in that wreckage! Get more men out there recovering the wounded.', 14, 0, 100, 22, 0, 27507, 60624, 0, 'Delora Lionheart');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64879, 4, 0, 'Lorewalker! What ever happened to Liu Lang?', 12, 0, 100, 1, 0, 0, 65654, 0, 'Yin to Lorewalker Amai');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64879, 5, 0, 'That\'s a sad story!', 12, 0, 100, 1, 0, 0, 65658, 0, 'Yin to Lorewalker Amai');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64879, 3, 0, 'But we don\'t explore very much anymore, do we?', 12, 0, 100, 1, 0, 0, 65649, 0, 'Yin to Lorewalker Amai');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64879, 2, 0, 'But isn\'t Pandaria hidden somewhere? Nobody can find it!', 12, 0, 100, 1, 0, 0, 65646, 0, 'Yin to Lorewalker Amai');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64879, 0, 0, 'Shen-zin Su!', 12, 0, 100, 1, 0, 0, 65635, 0, 'Yin to Lorewalker Amai');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64879, 1, 0, '...Whenever he got hungry?', 12, 0, 100, 1, 0, 0, 65645, 0, 'Yin to Lorewalker Amai');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64880, 6, 0, 'No you don\'t. It\'s full of trolls.', 12, 0, 100, 1, 0, 0, 65652, 0, 'Nan to Lorewalker Amai');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64880, 7, 0, 'What? What?', 12, 0, 100, 1, 0, 0, 65656, 0, 'Nan to Lorewalker Amai');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64880, 5, 0, 'Like Chen and Li Li Stormstout!', 12, 0, 100, 1, 0, 0, 65650, 0, 'Nan to Lorewalker Amai');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64880, 4, 0, 'Because sea turtles always return to the beach where they were born!', 12, 0, 100, 1, 0, 0, 65647, 0, 'Nan to Lorewalker Amai');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64880, 2, 0, 'Even Hao?', 12, 0, 100, 6, 0, 0, 65641, 0, 'Nan to Lorewalker Amai');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64880, 3, 0, 'Blerch! I\'d rather jump off this bridge.', 12, 0, 100, 1, 0, 0, 65644, 0, 'Nan to Lorewalker Amai');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64880, 1, 0, 'Sheesh, Hao. Haven\'t you ever paid attention?', 12, 0, 100, 1, 0, 0, 65638, 0, 'Nan to Lorewalker Amai');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64880, 0, 0, 'What is the song about?', 12, 0, 100, 1, 0, 0, 65634, 0, 'Nan to Lorewalker Amai');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64881, 4, 0, 'Hey!', 12, 0, 100, 1, 0, 0, 65642, 0, 'Hao to Lorewalker Amai');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64881, 3, 0, 'Does the turtle know we\'re here?', 12, 0, 100, 1, 0, 0, 65640, 0, 'Hao to Lorewalker Amai');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64881, 2, 0, 'I just thought it was weird that the mountains had flippers!', 12, 0, 100, 1, 0, 0, 65639, 0, 'Hao to Lorewalker Amai');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64881, 1, 0, 'We live on the back of a big turtle?!', 12, 0, 100, 5, 0, 0, 65637, 0, 'Hao to Lorewalker Amai');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64881, 0, 0, 'Why can\'t I understand any of the words?', 12, 0, 100, 1, 0, 0, 65631, 0, 'Hao to Lorewalker Amai');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64881, 10, 0, 'That\'s un-possible!', 12, 0, 100, 1, 0, 0, 65657, 0, 'Hao to Lorewalker Amai');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64881, 9, 0, 'What?', 12, 0, 100, 1, 0, 0, 65655, 0, 'Hao to Lorewalker Amai');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64881, 8, 0, 'Oh. Well, forget it then.', 12, 0, 100, 1, 0, 0, 65653, 0, 'Hao to Lorewalker Amai');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64881, 5, 0, 'Yeah, you should all be more like me.', 12, 0, 100, 1, 0, 0, 65643, 0, 'Hao to Lorewalker Amai');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64881, 6, 0, 'Does that mean our moms and dads and grandparents were all explorers, too?', 12, 0, 100, 1, 0, 0, 65648, 0, 'Hao to Lorewalker Amai');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64881, 7, 0, 'I want to explore the world!', 12, 0, 100, 1, 0, 0, 65651, 0, 'Hao to Lorewalker Amai');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (65469, 0, 0, 'Such skill for a new trainee.', 12, 0, 100, 511, 0, 0, 56431, 0, 'Aspiring Trainee to Training Target');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (65469, 0, 1, 'That target didn\'t stand a chance.', 12, 0, 100, 509, 0, 0, 56433, 0, 'Aspiring Trainee to Training Target');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (65469, 0, 2, 'One day you will have to teach me your secrets.', 12, 0, 100, 509, 0, 0, 56432, 0, 'Aspiring Trainee to Training Target');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (65469, 0, 3, 'You are bound for a great things, trainee.', 12, 0, 100, 508, 0, 0, 56435, 0, 'Aspiring Trainee to Training Target');
